export const isoWeekdayPlugin = (_opt, dayjsClass) => {
    dayjsClass.prototype.isoWeekday = function (setWeekday) {
        const { $W } = this;
        const weekday = $W <= 0 ? $W + 7 : $W;
        if (setWeekday) {
            let diff = setWeekday - weekday;
            if (diff < 0)
                diff += 7;
            return this.add(diff, 'day');
        }
        return weekday;
    };
};
